mapper.select:
selectfrom house WHERE 1=1
AND house_name LIKE #{houseName}		
AND householder_name LIKE #{householderName}		

	AND (householder_phone =#{householderPhone} OR householder_cellphone =#{householderPhone})

AND loupan_id =#{loupanId}

select h.id, h.house_name, h.householder_name, h.householder_phone, h.householder_cellphone, h.loupan_id, l.loupan_Name, l.district_code
from house h,loupan l WHERE h.loupan_id=l.id
AND h.loupan_id =#{loupanId}
AND l.district${districtLevel} =#{districtCode}
